struct IOManager 
{
	void clockwiseLogo()
	{
		Locator::getDisplay()->drawRGBBitmap(1, 1, epd_bitmap_clockwise64, 63, 21);
	}
    
    void wifiConnecting()
    {
		Locator::getDisplay()->fillRect(0, 24, 64, 52, 0);
        Locator::getDisplay()->drawRGBBitmap(16, 24, epd_bitmap_wifi, 32, 32);
		printCenter("Checking WiFi", 61); 
    }

	void wifiConnectionFailed()
    {
		Locator::getDisplay()->fillRect(0, 24, 64, 52, 0);
        Locator::getDisplay()->drawRGBBitmap(16, 24, epd_bitmap_wifi_failed, 32, 32);		
		printCenter("WiFi failed", 61);
    }

	void ntpConnecting()
    {
		Locator::getDisplay()->fillRect(0, 24, 64, 52, 0);
        Locator::getDisplay()->drawRGBBitmap(16, 24, epd_bitmap_ntp, 32, 32);
		printCenter("NTP Server", 61);
    }

	void printCenter(const char *buf, int y)
	{
		int16_t x1, y1;
		uint16_t w, h;
		Locator::getDisplay()->setFont(&Picopixel);
		Locator::getDisplay()->getTextBounds(buf, 0, y, &x1, &y1, &w, &h);
		Locator::getDisplay()->setCursor(32-(w / 2), y);
		Locator::getDisplay()->setTextColor(0xffff);
		Locator::getDisplay()->print(buf);
	}

};